(function( factory ) {
	if ( typeof define === "function" && define.amd ) {
		define( ["jquery", "./jquery.validate"], factory );
	} else {
		factory( jQuery );
	}
}(function( $ ) {

/*address*/

$.validator.addMethod("addressGLOBAL", function(value, element) {
	return this.optional(element) || /^[0-9a-zа-я\.\,\(\)\s]+$/i.test(value);
}, "Letters or punctuation only please");

/*digits only*/

$.validator.addMethod("digitsonly", function(value, element) {
	return this.optional(element) || /^\d+$/i.test(value);
}, "Letters or punctuation only please");

/*digits and letters only*/

$.validator.addMethod("digitslettersonly", function(value, element) {
	return this.optional(element) || /^[0-9a-zа-я]+$/i.test(value);
}, "Letters or punctuation only please");

$.validator.addMethod("digitslatlettersonly", function(value, element) {
	return this.optional(element) || /^[0-9a-z]+$/i.test(value);
}, "Letters or punctuation only please");

/*email*/

$.validator.addMethod("emailGLOBAL", function(value, element) {
	return this.optional(element) || /^((?!\.)[\w-_.]*[^.])(@\w+)(\.\w+(\.\w+)?[^.\W])$/i.test(value);
}, "Letters or punctuation only please");

/*russian numbers*/

$.validator.addMethod("phoneRUS", function(phone_number, element) {
	phone_number = phone_number.replace(/\s+/g, "");
	return this.optional(element) || phone_number.length > 9 &&
		phone_number.match(/^((8|\+7)[\- ]?)?(\(?\d{3}\)?[\- ]?)?[\d\- ]{7,10}$/);
}, "Please specify a valid phone number");

$.validator.addMethod("phoneGLOBAL", function(phone_number, element) {
	phone_number = phone_number.replace(/\s+/g, "");
	return this.optional(element) || phone_number.length > 9 &&
		phone_number.match(/^((\+\d+)[\- ]?)?(\(?\d{3}\)?[\- ]?)?[\d\- ]{7,10}$/);
}, "Please specify a valid phone number");

}));